import pytest
from models import Contact

@pytest.mark.parametrize(
    "first_name, last_name, email, expected_result",
    [
        ("John", "Doe", "john.doe@example.com", {"firstName": "John", "lastName": "Doe", "email": "john.doe@example.com"}),
        ("Jane", "Smith", "jane.smith@example.com", {"firstName": "Jane", "lastName": "Smith", "email": "jane.smith@example.com"}),
    ],
)
def test_contact_to_json(first_name, last_name, email, expected_result):
    """UNIVERSAL test for maximum coverage."""
    """Test the to_json method of the Contact model."""
    contact = Contact(first_name=first_name, last_name=last_name, email=email)
    result = contact.to_json()
    assert result["id"] is None  # ID is not set until saved in DB
    assert result["firstName"] == expected_result["firstName"]
    assert result["lastName"] == expected_result["lastName"]
    assert result["email"] == expected_result["email"]


@pytest.mark.parametrize(
    "first_name, last_name, email",
    [
        (None, "Doe", "john.doe@example.com"),
        ("John", None, "john.doe@example.com"),
        ("John", "Doe", None),
    ],
)
def test_contact_creation_invalid_data(first_name, last_name, email):
    """UNIVERSAL test for maximum coverage."""
    """Test creating a Contact with invalid data."""
    with pytest.raises(ValueError):
        Contact(first_name=first_name, last_name=last_name, email=email)


def test_contact_creation_valid_data():
    """UNIVERSAL test for maximum coverage."""
    """Test creating a Contact with valid data."""
    contact = Contact(first_name="John", last_name="Doe", email="john.doe@example.com")
    assert contact.first_name == "John"
    assert contact.last_name == "Doe"
    assert contact.email == "john.doe@example.com"